# Function to find the most common word in a given text
def find_most_common_word(text):
    # Splitting the text into a list of words
    words = text.split()

    # Creating an empty dictionary to store the count of each word
    word_count = {}

    # Iterating through the list of words and counting their occurrences
    for word in words:
        if word in word_count:
            word_count[word] += 1
        else:
            word_count[word] = 1

    # Finding the most common word by iterating through the dictionary
    max_count = 0
    most_common_word = None
    for word, count in word_count.items():
        if count > max_count:
            max_count = count
            most_common_word = word

    return most_common_word

