import random

def generate_maze(width, height):
    maze = [['#'] * (width*2+1) for _ in range(height*2+1)]

    def add_wall(x, y):
        if 0 < x < width*2 and 0 < y < height*2:
            walls.append((x, y))

    def carve_passage(x, y):
        maze[y][x] = ' '
        add_wall(x-1, y)
        add_wall(x+1, y)
        add_wall(x, y-1)
        add_wall(x, y+1)

    start_x, start_y = random.randint(0, width-1)*2+1, random.randint(0, height-1)*2+1
    carve_passage(start_x, start_y)
    walls = []
    while walls:
        wall_x, wall_y = random.choice(walls)
        walls.remove((wall_x, wall_y))
        opposite_x, opposite_y = None, None
        if wall_x % 2 == 0:
            if maze[wall_y][wall_x-1] == maze[wall_y][wall_x+1]:
                opposite_y = wall_y
                opposite_x = wall_x + random.choice([-1, 1])
            else:
                opposite_y = wall_y
                opposite_x = wall_x
        else:
            if maze[wall_y-1][wall_x] == maze[wall_y+1][wall_x]:
                opposite_y = wall_y
                opposite_x = wall_x + random.choice([-1, 1])
            else:
                opposite_y = wall_y
                opposite_x = wall_x
        if maze[opposite_y][opposite_x] == '#':
            carve_passage(opposite_x, opposite_y)

    return maze
